class Hover3D{constructor(a){this.id=a,this.xOffset=10,this.yOffset=10,this.attack=.1,this.release=.5,this.perspective=500,this.create()}create(){document.querySelectorAll(this.id).forEach(a=>{const b=a.getBoundingClientRect(),c="perspective("+this.perspective+"px) ";a.style.setProperty("transform-style","preserve-3d"),a.addEventListener("mouseenter",()=>{a.style.setProperty("transition","transform "+this.attack+"s")}),a.addEventListener("mousemove",d=>{let e=d.clientY-b.top,f=d.clientX-b.left,g=this.map(f,0,b.width,-this.xOffset,this.xOffset),h=this.map(e,0,b.height,this.yOffset,-this.yOffset);a.style.setProperty("transform",c+("rotateX("+h+"deg) ")+("rotateY("+g+"deg)"))}),a.addEventListener("mouseleave",()=>{a.style.setProperty("transition","transform "+this.release+"s"),a.style.setProperty("transform",c+"rotateX(0deg) rotateY(0deg)")})})}map(a,b,c,d,e){return d+(e-d)*((a-b)/(c-b))}}