function testimonialInit(e){let t=e.querySelectorAll(".tpgb-testimonials");t&&t.forEach(s=>{var o=s.querySelector(".post-loop-inner");if(s.classList.contains("tpgb-isotope")||s.classList.contains("tpgb-carousel")){let e=s.querySelectorAll(".testi-readbtn");e.forEach(e=>{e.addEventListener("click",function(e){let t=e.currentTarget,i=t.previousSibling,n=JSON.parse(t.dataset.readdata);"none"!=i.style.display?(i.style.display="none",t.innerHTML=n.readMore):(i.style.display="inline",t.innerHTML=n.readLess),s.classList.contains("tpgb-isotope")&&new Isotope(o,{itemSelector:".grid-item",resizable:!0,sortBy:"original-order"}).layout()})})}})}document.addEventListener("DOMContentLoaded",e=>{testimonialInit(document)});