window.addEventListener("DOMContentLoaded",()=>{tpsocialShare(document)});function tpsocialShare(a){let b=a.querySelectorAll(".tpgb-social-sharing");b&&b.forEach(a=>{let b=a.querySelector(".tpgb-social-list");if(a.classList.contains("sharing-toggle"))if(a.classList.contains("sharing-style-1")){let c=a.querySelector(".toggle-share");c.addEventListener("click",a=>{b.classList.contains("active")?(b.classList.remove("active"),slideUpFlex(b,300)):(b.classList.add("active"),slideDownFlex(b,300)),a.currentTarget.classList.contains("menu-active")?a.currentTarget.classList.remove("menu-active"):a.currentTarget.classList.add("menu-active")})}else if(a.classList.contains("sharing-style-2")){let c=a.querySelector(".tpgb-main-menu");c.addEventListener("click",()=>{b.classList.contains("active")?b.classList.remove("active"):b.classList.add("active")})}})}