;var wpsFtUpdater={};wpsFtUpdater.start=function(b,e){var c=jQuery("#wps-ft-updater-status"),a=jQuery("#wps-ft-updater-update"),d=jQuery("#wps-ft-updater-blinker");d.addClass("blinker");c.html("<p>Database update is in progress &hellip;</p>");jQuery(".wps-ft-update-button").prop("disabled",true);jQuery.ajax({type:"POST",url:b,data:{},complete:function(){wpsFtUpdater.generating=false;d.removeClass("blinker")},success:function(f){if(typeof f.time!=="undefined"){a.html("<p>Database update has been completed ("+f.time+"s).</p>");if(typeof f.errors!=="undefined"&&Array.isArray(f.errors)){a.append('<p style="color:#f00">'+f.errors.join("<br/>")+"</p>")}if(typeof f.notices!=="undefined"&&Array.isArray(f.notices)){a.append('<p style="color:#060">'+f.notices.join("<br/>")+"</p>")}if(typeof e!=="undefined"){a.append('<p>Click <a class="button button-primary" href="'+e+'">Continue</a> to proceed.</p>');jQuery("input.button.button-primary").hide();jQuery("div.wps-performance-options").hide();jQuery("div.wps-ft-options").hide()}}},dataType:"json"})};