jQuery(function($){function runAjaxAction(button,action){button=$(button);var panel=button.closest(".puc-debug-bar-panel-v4");var responseBox=button.closest("td").find(".puc-ajax-response");responseBox.text("Processing...").show();$.post(ajaxurl,{action:action,uid:panel.data("uid"),_wpnonce:panel.data("nonce")},function(data){responseBox.html(data)},"html")}$('.puc-debug-bar-panel-v4 input[name="puc-check-now-button"]').click(function(){runAjaxAction(this,"puc_v4_debug_check_now");return false});$('.puc-debug-bar-panel-v4 input[name="puc-request-info-button"]').click(function(){runAjaxAction(this,"puc_v4_debug_request_info");return false});var panels=$("#debug-menu-targets").find(".puc-debug-bar-panel-v4");panels.each(function(){var panel=$(this);var uid=panel.data("uid");var target=panel.closest(".debug-menu-target");target.attr("id","debug-menu-target-puc-"+uid);$("#debug-bar-menu").find(".puc-debug-menu-link-"+uid).closest(".debug-menu-link").attr("id","debug-menu-link-puc-"+uid).attr("href","#"+target.attr("id"))})});